import React, { useState } from "react";
import axios from "axios";

function ComicSearch() {
  const [date, setDate] = useState("");
  const [comics, setComics] = useState([]);

  const searchComics = () => {
    axios
      .get(`http://localhost:8080/api/comics/new-releases?date=${date}`)
      .then((response) => {
        setComics(response.data);
      })
      .catch((error) => {
        console.error("There was an error fetching the comics!", error);
      });
  };

  return (
    <div>
      <h1>Search Comics by Release Date</h1>
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <button onClick={searchComics}>Search</button>

      <div>
        {comics.length > 0 ? (
          <ul>
            {comics.map((comic, index) => (
              <li key={index}>
                <h2>{comic.title}</h2>
                <p>Publisher: {comic.publisher}</p>
                <p>Release Date: {comic.release_date}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No comics found for the selected date.</p>
        )}
      </div>
    </div>
  );
}

export default ComicSearch;
