import React, { useState } from "react";
import axios from "axios";
import './index.css';

function ComicSearch() {
  const [date, setDate] = useState("");
  const [publisher, setPublisher] = useState("");
  const [title, setTitle] = useState("");
  const [creators, setCreators] = useState("");
  const [comics, setComics] = useState([]);

  const searchComics = () => {
    // Construct the query parameters
    const params = new URLSearchParams();
    if (date) params.append("date", date);
    if (publisher) params.append("publisher", publisher);
    if (title) params.append("title", title);
    if (creators) params.append("creators", creators);

    axios
      .get(`http://localhost:8080/api/comics/new-releases?${params.toString()}`)
      .then((response) => {
        console.log("API Response: ", response.data);
        // Assuming the response structure is adjusted to match your ComicController
        setComics(response.data.results || []); // Use `results` or whatever the API returns
      })
      .catch((error) => {
        console.error("There was an error fetching the comics!", error);
      });
  };

  const handleSearchComic = (diamondId) => {
    const searchUrl = `https://www.google.com/search?q=comic+${diamondId}`;
    window.open(searchUrl, "_blank"); // Opens the search in a new tab
  };

  return (
    <div className="main">
      <div>
        <label>
          Release Date:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </label>
        <label>
          Publisher:
          <input
            type="text"
            value={publisher}
            onChange={(e) => setPublisher(e.target.value)}
          />
        </label>
        <label>
          Title:
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </label>
        <label>
          Creators:
          <input
            type="text"
            value={creators}
            onChange={(e) => setCreators(e.target.value)}
          />
        </label>
        <button onClick={searchComics}>Search</button>
      </div>

      <div>
        {comics.length > 0 ? (
          <ul>
            {comics.map((comic, index) => (
              <li key={index}>
                <h2>{comic.title}</h2>
                <p>Publisher: {comic.publisher}</p>
                <p>Release Date: {comic.release_date}</p>
                <p>Description: {comic.description || "No description available."}</p>
                <p>Creators: {comic.creators ? comic.creators.join(", ") : "Unknown"}</p>
                {/* Link to search the comic using diamond_id */}
                <button onClick={() => handleSearchComic(comic.diamond_id)}>
                  Search comic
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No comics found.</p>
        )}
      </div>
    </div>
  );
}

export default ComicSearch;
