import React, { useState } from "react";
import axios from "axios";

function ComicSearch() {
  const [date, setDate] = useState("");
  const [publisher, setPublisher] = useState("");
  const [title, setTitle] = useState("");
  const [creators, setCreators] = useState("");
  const [comics, setComics] = useState([]);

  const searchComics = () => {
    console.log("Searching for comics with the following parameters:");
    console.log("Date:", date);
    console.log("Publisher:", publisher);
    console.log("Title:", title);
    console.log("Creators:", creators);

    // Build the query string based on input
    let query = `?date=${date}`;
    if (publisher) query += `&publisher=${encodeURIComponent(publisher)}`;
    if (title) query += `&title=${encodeURIComponent(title)}`;
    if (creators) query += `&creators=${encodeURIComponent(creators)}`;

    axios
      .get(`http://localhost:8080/api/comics/new-releases${query}`)
      .then((response) => {
        console.log("API Response:", response.data);

        if (response.data.comics && response.data.comics.length > 0) {
          setComics(response.data.comics);
        } else {
          console.log("No comics found for the selected parameters.");
          setComics([]);
        }
      })
      .catch((error) => {
        console.error("There was an error fetching the comics!", error);
      });
  };

  return (
    <div>
      <h1>Search Comics</h1>
      <div>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          placeholder="Release Date"
        />
      </div>
      <div>
        <input
          type="text"
          value={publisher}
          onChange={(e) => setPublisher(e.target.value)}
          placeholder="Publisher"
        />
      </div>
      <div>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Comic Title"
        />
      </div>
      <div>
        <input
          type="text"
          value={creators}
          onChange={(e) => setCreators(e.target.value)}
          placeholder="Creators"
        />
      </div>
      <button onClick={searchComics}>Search</button>

      <div>
        {comics.length > 0 ? (
          <ul>
            {comics.map((comic, index) => (
              <li key={index}>
                <h2>{comic.title}</h2>
                <p>Publisher: {comic.publisher}</p>
                <p>Description: {comic.description}</p>
                <p>Price: {comic.price}</p>
                <p>Creators: {comic.creators}</p>
                <p>Release Date: {comic.release_date}</p>
                <p>Diamond ID: {comic.diamond_id}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No comics found for the selected parameters.</p>
        )}
      </div>
    </div>
  );
}

export default ComicSearch;
