import React, { useState } from "react";
import axios from "axios";

function ComicSearch() {
  const [date, setDate] = useState("");
  const [comics, setComics] = useState([]);

  const searchComics = () => {
    axios
      .get(`http://localhost:8080/api/comics/new-releases?date=${date}`)
      .then((response) => {
        setComics(response.data.comics); // Asegúrate de acceder a la propiedad correcta aquí
      })
      .catch((error) => {
        console.error("There was an error fetching the comics!", error);
      });
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Search Comics by Release Date</h1>
      <div className="input-group mb-3">
        <input
          type="date"
          className="form-control"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <button className="btn btn-primary" onClick={searchComics}>
          Search
        </button>
      </div>

      <div>
        {comics.length > 0 ? (
          <ul className="list-group">
            {comics.map((comic, index) => (
              <li key={index} className="list-group-item">
                <h2>{comic.title}</h2>
                <p><strong>Publisher:</strong> {comic.publisher}</p>
                <p><strong>Description:</strong> {comic.description}</p>
                <p><strong>Release Date:</strong> {comic.release_date}</p>
                <p><strong>Price:</strong> {comic.price}</p>
                <p><strong>Creators:</strong> {comic.creators}</p>
                <a
                  href={`https://www.previewsworld.com/Search?diamondid=${comic.diamond_id}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-secondary"
                >
                  Search Comic
                </a>
              </li>
            ))}
          </ul>
        ) : (
          <p>No comics found for the selected date.</p>
        )}
      </div>
    </div>
  );
}

export default ComicSearch;
